mapper.select:
SELECT COUNT(*) FROM ( SELECT g.game_id FROM ag_games g
JOIN (SELECT c.game_id FROM a_gamer_accounts ga JOIN ag_competitors c ON ga.gamer_id = c.gamer_id WHERE ga.user_name LIKE #{userName}
UNION SELECT i.game_id FROM a_gamer_accounts ga JOIN ag_invitations i ON ga.gamer_id = i.invitee_id WHERE ga.user_name LIKE #{userName} UNION SELECT i.game_id FROM a_gamer_accounts ga JOIN ag_invitations i ON ga.gamer_id = i.inviter_id WHERE ga.user_name LIKE #{userName}
) AS cc ON cc.game_id = g.game_id
g.game_variant_id = #{gameVariantId}
AND g.rules_version = #{rulesVersion}
AND g.competitors_quantity = #{playersNumber}
AND g.game_status_id = #{gameStatus} AND
g.initiation_method = #{initiationMethod}
AND
(
#{gamerId} IN (SELECT cmp1.gamer_id FROM ag_competitors cmp1 WHERE cmp1.game_id = g.game_id AND cmp1.is_initiator = 1)
OR
#{gamerId} NOT IN (SELECT cmp1.gamer_id FROM ag_competitors cmp1 WHERE cmp1.game_id = g.game_id AND cmp1.is_initiator = 1)
)
AND
(
#{gamerId} IN (SELECT cmp1.gamer_id FROM ag_competitors cmp1 WHERE cmp1.game_id = g.game_id AND cmp1.is_initiator = 1)
OR
#{gamerId} IN (SELECT i1.invitee_id FROM ag_invitations i1 WHERE i1.game_id = g.game_id AND i1.status_id IN (0, 1))
)
#{gamerId} IN (SELECT cmp1.gamer_id FROM ag_competitors cmp1 WHERE cmp1.game_id = g.game_id)
UNION SELECT fg.game_id FROM fg_games fg
JOIN (SELECT c.game_id FROM a_gamer_accounts ga JOIN fg_competitors c ON ga.gamer_id = c.gamer_id WHERE ga.user_name LIKE #{userName}) AS cc ON cc.game_id = fg.game_id
fg.game_variant_id = #{gameVariantId}
AND fg.rules_version = #{rulesVersion}
AND fg.competitors_quantity = #{playersNumber}
AND #{gamerId} IN (SELECT cmp2.gamer_id FROM fg_competitors cmp2 WHERE cmp2.game_id = fg.game_id)
) x

SELECT g.game_id, g.game_label, gs.game_variant_id, gv.variant_name, gv.moves_quantity, gs.competitors_quantity, g.rounding, g.game_status_id, g.created_time, g.started_time, g.finished_time, g.initiation_method, ev2.updated_time, ev2.event_type, ev2.event_initiator_id, g.game_letter, gs.rules_version, gs.switch_move_order, gs.game_series_id FROM ag_games g JOIN ag_game_series gs ON gs.game_series_id = g.game_series_id JOIN gv_game_variants gv ON gs.game_variant_id = gv.game_variant_id
JOIN (SELECT c.game_id FROM a_gamer_accounts ga JOIN ag_competitors c ON ga.gamer_id = c.gamer_id WHERE ga.user_name LIKE #{userName}
UNION SELECT i.game_id FROM a_gamer_accounts ga JOIN ag_invitations i ON ga.gamer_id = i.invitee_id WHERE ga.user_name LIKE #{userName} UNION SELECT i.game_id FROM a_gamer_accounts ga JOIN ag_invitations i ON ga.gamer_id = i.inviter_id WHERE ga.user_name LIKE #{userName}
) AS cc ON cc.game_id = g.game_id
JOIN (SELECT ev1.game_id, ev1.updated_time, ev1.event_type, event_initiator_id FROM ag_events_view ev1 INNER JOIN (SELECT game_id, MAX(event_type) AS event_type, MAX(updated_time) AS updated_time FROM ag_events_view GROUP BY 1) AS ev ON ev.game_id = ev1.game_id AND ev.event_type = ev1.event_type AND ev.updated_time = ev1.updated_time) AS ev2 ON g.game_id = ev2.game_id
gs.game_variant_id = #{gameVariantId}
AND gs.rules_version = #{rulesVersion}
AND gs.competitors_quantity = #{playersNumber}
AND g.game_status_id = #{gameStatus} AND
g.initiation_method = #{initiationMethod}
AND
(
#{gamerId} IN (SELECT cmp1.gamer_id FROM ag_competitors cmp1 WHERE cmp1.game_id = g.game_id AND cmp1.is_initiator = 1)
OR
#{gamerId} NOT IN (SELECT cmp1.gamer_id FROM ag_competitors cmp1 WHERE cmp1.game_id = g.game_id AND cmp1.is_initiator = 1)
)
AND
(
#{gamerId} IN (SELECT cmp1.gamer_id FROM ag_competitors cmp1 WHERE cmp1.game_id = g.game_id AND cmp1.is_initiator = 1)
OR
#{gamerId} IN (SELECT i1.invitee_id FROM ag_invitations i1 WHERE i1.game_id = g.game_id AND i1.status_id IN (0, 1))
)
#{gamerId} IN (SELECT cmp1.gamer_id FROM ag_competitors cmp1 WHERE cmp1.game_id = g.game_id)
UNION SELECT fg.game_id, fg.game_label, fgs.game_variant_id, gv.variant_name, gv.moves_quantity, fgs.competitors_quantity, fg.rounding, 2 AS game_status_id, fg.created_time, fg.started_time, fg.finished_time, null AS initiation_method, ev2.updated_time, ev2.event_type, ev2.event_initiator_id, fg.game_letter, fgs.rules_version, fgs.switch_move_order, fgs.game_series_id FROM fg_games fg JOIN fg_game_series fgs ON fgs.game_series_id = fg.game_series_id JOIN gv_game_variants gv ON fgs.game_variant_id = gv.game_variant_id
JOIN (SELECT c.game_id FROM a_gamer_accounts ga JOIN fg_competitors c ON ga.gamer_id = c.gamer_id WHERE ga.user_name LIKE #{userName}) AS cc ON cc.game_id = fg.game_id
JOIN (SELECT ev1.game_id, ev1.updated_time, ev1.event_type, event_initiator_id FROM ag_events_view ev1 INNER JOIN (SELECT game_id, MAX(event_type) AS event_type, MAX(updated_time) AS updated_time FROM ag_events_view GROUP BY 1) AS ev ON ev.game_id = ev1.game_id AND ev.event_type = ev1.event_type AND ev.updated_time = ev1.updated_time) AS ev2 ON fg.game_id = ev2.game_id
fgs.game_variant_id = #{gameVariantId}
AND fgs.rules_version = #{rulesVersion}
AND fgs.competitors_quantity = #{playersNumber}
AND #{gamerId} IN (SELECT cmp2.gamer_id FROM fg_competitors cmp2 WHERE cmp2.game_id = fg.game_id)
ORDER BY
11
13
DESC

SELECT g.game_id, ga.user_name AS user_name,
p.avatar,
ga.is_bot, CASE WHEN ga.status_id > 1 THEN 1 ELSE 0 END AS is_removed, cmp.move_order, cmp.is_initiator, cmp.is_out, cr1.is_winner, cmp.joined_time, cr1.total_funds, cr2.is_winner AS is_series_winner, cr2.total_funds AS series_total_funds, CASE WHEN g.initiation_method = 1 AND cmp.is_initiator = 0 THEN 1 ELSE 0 END AS is_invitation, null AS inv_created_time, null AS inv_expired_time, 1 AS invitation_status FROM ag_competitors cmp JOIN a_gamer_accounts ga ON cmp.gamer_id = ga.gamer_id LEFT OUTER JOIN a_profiles p ON cmp.gamer_id = p.gamer_id JOIN ag_games g ON cmp.game_id = g.game_id LEFT OUTER JOIN (SELECT cr.is_winner, cr.total_funds, cr.gamer_id, cr.game_id FROM g_competitor_results cr) AS cr1 ON cr1.gamer_id = cmp.gamer_id AND cr1.game_id = g.game_id LEFT OUTER JOIN (SELECT cr.is_winner, cr.total_funds, cr.gamer_id, cr.game_series_id FROM g_competitor_results cr WHERE cr.game_id is null) AS cr2 ON cr2.gamer_id = cmp.gamer_id AND cr2.game_series_id = g.game_series_id WHERE g.game_id IN #{item}
UNION SELECT inv.game_id, inv.invitee_name AS user_name, inv.avatar, inv.is_bot, CASE WHEN inv.account_status_id > 1 THEN 1 ELSE 0 END AS is_removed, null AS move_order, 0 AS is_initiator, 0 AS is_out, 0 AS is_winner, null AS joined_time, null AS total_funds, 0 AS is_series_winner, null AS Series_total_funds, 1 AS is_invitation, inv.created_time AS inv_created_time, inv.expired_time AS inv_expired_time, inv.status_id FROM ag_games g1 JOIN (SELECT i.game_id, i.created_time, i.expired_time, ga1.user_name AS inviter_name, ga2.user_name AS invitee_name,
p.avatar,
ga2.is_bot, ga2.status_id AS account_status_id, i.status_id FROM ag_invitations i JOIN a_gamer_accounts ga1 ON i.inviter_id = ga1.gamer_id JOIN a_gamer_accounts ga2 ON i.invitee_id = ga2.gamer_id LEFT OUTER JOIN a_profiles p ON i.invitee_id = p.gamer_id WHERE i.status_id NOT IN (1, 3, 4)) AS inv ON inv.game_id = g1.game_id WHERE g1.game_id IN #{item}
UNION SELECT fg.game_id, ga.user_name AS user_name,
p.avatar,
ga.is_bot, CASE WHEN ga.status_id > 1 THEN 1 ELSE 0 END AS is_removed, fcmp.move_order, 0 AS is_initiator, fcmp.is_out, cr1.is_winner, null AS joined_time, cr1.total_funds, cr2.is_winner AS is_series_winner, cr2.total_funds AS series_total_funds, 0 AS is_invitation, null AS inv_created_time, null AS inv_expired_time, null AS invitation_status FROM fg_competitors fcmp JOIN a_gamer_accounts ga ON fcmp.gamer_id = ga.gamer_id LEFT OUTER JOIN a_profiles p ON fcmp.gamer_id = p.gamer_id JOIN fg_games fg ON fcmp.game_id = fg.game_id LEFT OUTER JOIN (SELECT cr.is_winner, cr.total_funds, cr.gamer_id, cr.game_id FROM g_competitor_results cr) AS cr1 ON cr1.gamer_id = fcmp.gamer_id AND cr1.game_id = fg.game_id LEFT OUTER JOIN (SELECT cr.is_winner, cr.total_funds, cr.gamer_id, cr.game_series_id FROM g_competitor_results cr WHERE cr.game_id is null) AS cr2 ON cr2.gamer_id = fcmp.gamer_id AND cr2.game_series_id = fg.game_series_id WHERE fg.game_id IN #{item}

SELECT g.game_variant_id, g.competitors_quantity, g.game_status_id, CASE WHEN (SELECT COUNT(c.gamer_id) FROM ag_competitors c WHERE c.game_id = g.game_id ) > 0 THEN 1 ELSE 0 END AS already_joined, g.initiation_method, COUNT(g.game_id) AS games_quantity FROM ag_games g WHERE (g.game_status_id = 0 OR g.game_status_id = 1)
AND g.initiation_method = #{initiationMethod}
GROUP BY 1, 2, 3, 4, 5
